function mobileView() {

  /**
   * get top level domain
   * 
   * @param {String} host
   * @returns {mobileView.getTld.hostArr}
   */
  this.getTld = function(host) {
    var hostArr = host.split('.');
    return hostArr[hostArr.length - 1];
  };
  
  /**
   * Cookie domain may not include the subdomains (www. or m.) so the cookie is readable from within both domains
   * 
   * @returns {String}
   */
  this.getCookieDomain = function() {
    var hostArr = this.host.split('.')
      , arrLength = hostArr.length
      , cookieDomain = hostArr[arrLength - 2] + '.' + hostArr[arrLength - 1];
      
    return cookieDomain;
  };

  this.userAgent = navigator.userAgent.toLowerCase();
  this.protocol = window.location.protocol;
  this.host = window.location.host;
  this.pathname = window.location.pathname;
  this.path = this.pathname.split('.');
  this.tld = this.getTld(this.host);
  this.outputmode = false;
  this.desktopHostsDlrRegex = /www\.deutschlandradio\.[a-z]{2,3}/;
  this.desktopHostsDlfRegex = /www\.deutschlandfunk\.[a-z]{2,3}/;
  this.desktopHostsDrkRegex = /www\.deutschlandradiokultur\.[a-z]{2,3}/;
  this.desktopHostsRegex = /www\.deutschlandfunk\.[a-z]{2,3}|www\.deutschlandradiokultur\.[a-z]{2,3}|www\.deutschlandradio\.[a-z]{2,3}/; // live
  this.mobileHosts = [ // live
    'm.deutschlandfunk.' + this.tld,
    'm.deutschlandradiokultur.' + this.tld
  ];
  this.isMobile = false;
  this.urlToMobileDetectScript = '//' + this.host + '/themes/dradio/mobileDetect/mobileDetect.php';

  var _self = this;

  /**
   * sets and returns the isMobile state
   * 
   * @param {String} isMobile
   * @returns {Boolean}
   */
  this.setIsMobile = function (isMobile) {
    if (parseInt(isMobile) === 1) {
      this.isMobile = true;
    }
    return this.isMobile;
  };
  
  /**
   * try to set output mode and return it
   * 
   * @returns {Boolean|mobileView@pro;pathname@call;split}
   */
  this.getOutputMode = function() {
    if (this.outputmode === false) {
      if (typeof this.path[3] !== 'undefined') {
        this.outputmode = this.path[3];
      } else if (typeof this.path[2] !== 'undefined') {
        this.outputmode = this.path[2];
      }
    }
    return this.outputmode;
  };
  
  /**
   * returns false if element is not in array, else returns index
   * 
   * @param {String} item
   * @param {Array} arr
   * @returns {Boolean|Number}
