  this.in_array = function(item, arr) {
    var result = false;
    for (var key = 0; key < arr.length; key++) {
      if (item === arr[key]) {
        result = key;
      }
    }
    return result;
  };

  /**
   * determines if user is on desktop page
   * 
   * @returns {Boolean}
   */
  this.isOnDesktopSite = function() {
    var isOnDesktopSite = false;
