  };

  /**
   * redirects to mobile pages
   * 
   * @returns {undefined}
   */
  this.goToMobile = function () {
    
    var mobileHost;
    if (this.desktopHostsDrkRegex.test(this.host)) {
      mobileHost = this.mobileHosts[1];
    } else {
      mobileHost = this.mobileHosts[0];
    }

    var desktopArray = new Array(
      '187', // 0) DLF Startseite
      '215', // 1) DLR Hilfe
      '223', // 2) DLR Impressum
      '359', // 3) DLR Kontakt
      '260', // 4) DLR Audioarchiv
      '354', // 5) DLF Presseschau
