    } else {
      var key = this.in_array(this.path[1], desktopArray);
      if (key !== false) {
        if (key === 7 || key === 8 || key === 9) {
          newLocationHref = this.protocol + '//' + this.host + '/' + mobileArray[key];
        } else {
          newLocationHref = this.protocol + '//' + mobileHost + '/' + mobileArray[key];
        }
      }
    }

