/* Adding file: script/dlf2015/dlf2015.custom.js */
$(function () {
  
  /* choosing time in news look up page */
  $('body').on('change', 'select.time-select', function () {
    var time = $(this).val()
      , date = $(this).data('date')
      , host = window.location.host
      , path = window.location.pathname;
    window.location = 'http://' + host + path 
      + '?drn:date=' + date
      + '&drn:time=' + time
      + '#date-' + date;
  });
  
  /* choosing month or year in generic calendar box */
  $('body').on('change', '#calendarmonth, #calendaryear', function () {
    var href = $(this).val()
      , boxId = $(this).parent().parent().data('boxid')
      , separator = ( href.indexOf('?') > - 1 ) ? String.fromCharCode(38) : '?'
      , url = href+separator+'boxId='+boxId;
    $('div.dlf-calendar').css({opacity:0.2});
    $.ajax({
      url: url,
      success: function (response) {
        var source = $('<div>'+response+'</div>');
        $('div.dlf-calendar:first').html($('div.dlf-calendar', response).html()).css({opacity:1.0});
        $("#calendarmonth, #calendaryear").chosen({disable_search_threshold: 200});
      }
    });
  });
  
  $('a.linkAudio').each(function() {
    $(this).append(' <span>[AUDIO]</span>');
  });
  
  /*
   * show by css hidden playbuttons (these may only not be shown if js is deactivated)
   */
  $('.playbutton-hidden').removeClass('playbutton-hidden');

  $('body').on('click', '[href^="#"]', function(event) {
    event.preventDefault();
    if ( $(this).attr('href') != '#' &&  $(this).attr('href') != '#tabs-1' && $(this).attr('href') != '#tabs-2' && $(this).attr('href') != '#tabs-3') {
      window.location.hash = $(this).attr('href');
      var y = $(window).scrollTop(); //your current y position on the page
      $(window).scrollTop(y-50);
    }
    return false; 
  });
  
  if ( (window.location.pathname == '/') || (window.location.pathname.search(/\.187(\.de)?\.html/) >=0 ) ) {
    var articleArray = [];
    $('article[id^="article"]').each(function () {
      var a = $(this);
      var tempArray = a.attr('id').split('-');
      var index = $.inArray(tempArray[1], articleArray);
      if (index > -1) {
        if ($(this).hasClass('dlf-sidearticle')) {
          var p = a.parent();
          a.remove();
          var articles = p.find('article');
          if ( articles.length == 0 ) {
            p.remove();
          } else if ( articles.length > 1 ) {
            var next = $(articles[0]);
            if ( next.hasClass('hidden') ) {
              next.removeClass('hidden').addClass('lastVisible');
            }
          }
        }
      }
      articleArray.push(tempArray[1]);
    });
  } else if ( window.location.search.search(/dram:article_id=\d+$/) >= 0 ) {
    var m = window.location.search.match(/dram:article_id=(\d+)$/);
    $('div[id^="article"]').each(function () {
      var a = $(this);
      var tempArray = a.attr('id').split('-');
      if (tempArray[1] == m[1]) {
        if (a.hasClass('slide')) {
          a.remove();
        }
      }
    });
  }

  $("iframe[src^='https://www.youtube'], [src^='http://www.youtube']").each(function(){
     var iframe_src = $(this).attr('src');
     var wmode = "wmode=transparent";
     if(iframe_src.indexOf('?') !== -1) $(this).attr('src',iframe_src+'&'+wmode);
     else $(this).attr('src',iframe_src+'?'+wmode);
  });
});

/* Adding file: script/dlf-audioplayer.js */
$(document).ready(function() {
  dlfAudio = {
    //adjust these to the values needed by the page
    //playLogo : '',
    //pauseLogo : 'pause',
    //playLogoEmbed : '',
    //pauseLogoEmbed : 'pause',
    hidePlayerDelayInSeconds : 10,
    textSpeed : 40,  // lower = faster
    stationCodes :   ['dlf',                    'drk',                           'dradiowissen',    'nleicht'],
    stationMapping : ['DLF',                    'DRK',                           'DRW',             'DNL'],
    stationDomains : {
      'DLF' : ['deutschlandfunk','dlf'],
      'DRK' : ['deutschlandradiokultur','dkultur','drk',],
      'DRW' : ['dradiowissen.de','wissen','fe-stage','fe-dev',],
      'DNL' : ['nachrichtenleicht'],
    },

    //these are internal and should be left alone
    playerId : 'dlf-aod',
    currentPlayingImage : '',
    hidePlayerTimeout : '',
    textWidth : 0,

    jwplayerLogging : false, //enable/disable jwplayer logging

    ati : {
      richname : '',
      position : 0,
      startposition : 0,
      bufferposition : 0,
      isbuffering : 0,
      pollDelayInSeconds : 10,
      isPlaying : false,
      didFirePlayEvent : false,
    },

    ivw : {
      code : '012'
    },

    initialize : function() {
      jwplayer.key="mw0Kv9eMPxXAfLyDMTjUvtXdilzPDhk/m52fpg==";

      jwplayer.utils.log = function(){
        if(dlfAudio.jwplayerLogging) {
          var args = Array.prototype.slice.call(arguments, 0);
          if (typeof console.log == 'function') {
            console.log(args+(new Error()).stack);
          } else {
            args.push((new Error()).stack);
            console.log.apply(console, args);
          }
        }
      };

      $($('.'+dlfAudio.playerId)[0]).attr('id', dlfAudio.playerId);
      var dlfPlayerMain = $('.dlf-player-main');
      var value = dlfPlayerMain.data('hideplayerdelayinseconds');
      if(value) {
        if(value>2147483) {
          value = 2147483; //2147483647
        }
        dlfAudio.hidePlayerDelayInSeconds = value;
      }
      value = dlfPlayerMain.data('textspeed');
      if(value) {
        dlfAudio.textSpeed = value;
      }

      $('.dlf-player-state').click(function(){
        dlfAudio.preventPlayerHide();
        jwplayer(dlfAudio.playerId).play();
      });

      $('.dlf-player-progressbar').click(function(event){
        dlfAudio.preventPlayerHide();
        var element = $(this);
        var p = Math.floor(100 / element.width() * (event.pageX - element.offset().left));
        jwplayer(dlfAudio.playerId).seek(
          jwplayer(dlfAudio.playerId).getDuration() / 100 * p
        );
      });

      $('.dlf-player-embed').each(function(i,e) {
        $(e).click(function(ev){
          ev.preventDefault();
          var el = $(this);
          if (!el.data('isPlaying')) {
            el.data('isPlaying', true);
            if (dlfAudio.ati.isPlaying) {
              dlfAudio.fireATIRequest(
                'pause',
                dlfAudio.ati.position,
                dlfAudio.ati.startposition,
                dlfAudio.ati.bufferposition,
                dlfAudio.playerId
              );
              dlfAudio.ati.isPlaying = false;
            }
            dlfAudio.ati.didFirePlayEvent = false;
            var href = el.data('audio-src');
            if (el.data('audio-delivery-mode') == 'stream') {
              $('.dlf-player-download').removeAttr('href');
              $('.dlf-player-download').addClass('nodl');
            } else {
              $('.dlf-player-download').attr('href', href);
              $('.dlf-player-download').removeClass('nodl');
            }
            dlfAudio.startAnimateTitle(el.data('audio-title'));
            if (dlfAudio.currentPlayingImage != '') {
              dlfAudio.currentPlayingImage.data('isPlaying', false);
              jwplayer(dlfAudio.playerId).load([{file:href}]);
            } else {
              jwplayer(dlfAudio.playerId).load([{file:href}]);
            }
            dlfAudio.currentPlayingImage = el;
          }
          jwplayer(dlfAudio.playerId).play();
        });
      });

      $('.dlf-player-download').click(function(event){
        dlfAudio.xtdl();
      });

      var richname = '';
      if (typeof(xt_chap) != 'undefined' && xt_chap != '') {
        var matched = xt_chap.match(/.*%3A%3A(.*)/);
        if (typeof(matched[1]) != 'undefined' && matched[1] != '') {
          richname += matched[1];
        } else {
          richname += xt_chap;
        }
      }
      if (richname == '' && typeof(xtcustom.page_name) != 'undefined' && xtcustom.page_name != '') {
        richname += xtcustom.page_name;
      }
      dlfAudio.ati.richname = richname;

      firstAudioHref = $('.dlf-player-embed').first().data('audio-src');
      if (typeof firstAudioHref !== 'undefined' && firstAudioHref != '') {
        dlfAudio.setupPlayer(firstAudioHref);
      }
