    startAnimateTitle : function(title){
      //workaround to make sure text is in textbox when calculating textboxwidth
      $('.dlf-player-progressbar-text-inner').text(
        title+String.fromCharCode(160)
      );
      $('.dlf-player-progressbar-text-outer').each(function(i, element){
        var innerText = $(element).find('.dlf-player-progressbar-text-inner');
        var innerText0 = $(innerText[0]);
        var innerText1 = $(innerText[1]);
        innerText0.stop();
        innerText1.stop();
        innerText0.css('left','0');
        innerText1.css('left','100%');
      });

      //
    },

    animateTitle : function(outerText){
      var outerTextRec = outerText;
      outerText = null;
      var innerText = $(outerTextRec).find('.dlf-player-progressbar-text-inner');
      var innerText0 = $(innerText[0]);
      var innerText1 = $(innerText[1]);
      innerText0.stop();
      innerText1.stop();
      dlfAudio.textWidth = innerText.width();
      innerText0.css('left','0');
      innerText1.css('left',dlfAudio.textWidth+'px');
      if(dlfAudio.textWidth > $(outerTextRec).width()) {
        var realDuration = dlfAudio.textWidth  * dlfAudio.textSpeed;
        innerText0.animate({
          left : '-'+dlfAudio.textWidth
          },{
            duration : realDuration,
            easing : 'linear'
          }
        );
        innerText1.animate({
          left : '0'
          },{
            duration : realDuration,
            complete : function(){
              dlfAudio.animateTitle(outerTextRec);
            },
            easing : 'linear'
          }
        );
      } else {
        innerText1.css('left','100%');
      }
    },

    preventPlayerHide : function(){
      if (dlfAudio.hidePlayerTimeout != '') {
        window.clearTimeout(dlfAudio.hidePlayerTimeout);
        dlfAudio.hidePlayerTimeout = '';
      }
    },

    onPlay : function(){
      $('.dlf-player-state').addClass('pause');
      if ($(dlfAudio.currentPlayingImage).parent().hasClass('dlf-playbutton')) {
        dlfAudio.currentPlayingImage.parent().addClass('pause');
      }
      $('.dlf-player-main').show();
      $('.dlf-livesendung').hide();
      window.setTimeout(function(){
        $('.dlf-player-progressbar-text-outer').each(function(i, element){
          dlfAudio.animateTitle(element);
        });
      }, 1000);
      dlfAudio.preventPlayerHide();
    },

    onPause : function(){
      $('.dlf-player-state').removeClass('pause');
      if ($(dlfAudio.currentPlayingImage).parent().hasClass('dlf-playbutton')) {
        dlfAudio.currentPlayingImage.parent().removeClass('pause');
      }
      dlfAudio.preventPlayerHide();
      dlfAudio.hidePlayerTimeout = window.setTimeout(function(){
        dlfAudio.hidePlayerTimeout = '';
        $('.dlf-player-main').hide();
        $('.dlf-livesendung').show();
        dlfAudio.startAnimateTitle(dlfAudio.currentPlayingImage.data('audio-title'));
      },dlfAudio.hidePlayerDelayInSeconds * 1000);
    },

