            dlfAudio.onPlay();
            dlfAudio.fireIvwRequest();
          },
          onBuffer: function() {
            dlfAudio.onPlay();
          },
          onPause: function() {
            dlfAudio.fireATIRequest(
              'pause',
              dlfAudio.ati.position,
              dlfAudio.ati.startposition,
              dlfAudio.ati.bufferposition,
              dlfAudio.playerId
            );
            dlfAudio.ati.isPlaying = false;
            dlfAudio.onPause();
            dlfAudio.fireIvwRequest();
          },
          onIdle: function() {
            dlfAudio.onPause();
          },
          onTime: function(a) {
            if (!dlfAudio.ati.isPlaying) {
              if (dlfAudio.ati.didFirePlayEvent) {
                dlfAudio.ati.position = a.position;
                dlfAudio.ati.startposition = a.position;
              }
              var buffer = jwplayer(dlfAudio.playerId).getBuffer();
              var duration =
                dlfAudio.currentPlayingImage.data('audio-duration') > 0 ?
                parseInt(dlfAudio.currentPlayingImage.data('audio-duration')) :
                parseInt(a.duration);
              dlfAudio.ati.bufferposition = buffer <= 0 ? 0 :
                (buffer / 100 * duration);
              dlfAudio.fireATIRequest(
                'play&buf=1',
                dlfAudio.ati.position,
                dlfAudio.ati.startposition,
                dlfAudio.ati.bufferposition,
                dlfAudio.playerId
              );
              dlfAudio.ati.isbuffering = buffer >= 100 ? 0 : 1;
              if (!dlfAudio.ati.isbuffering) {
                dlfAudio.fireATIRequest(
                  'info&buf=' + dlfAudio.ati.isbuffering,
                  dlfAudio.ati.position,
                  dlfAudio.ati.startposition,
                  dlfAudio.ati.bufferposition,
                  dlfAudio.playerId
                );
              }
              dlfAudio.ati.didFirePlayEvent = true;
            }
            dlfAudio.ati.isPlaying = true;
            dlfAudio.ati.position = a.position;

            $('.dlf-player-progressbar-runner').width((100/a.duration*a.position)+'%');

            var r = a.duration - a.position;
            var pm = Math.floor(r/60);
            var ps = Math.floor(r%60);
            $('.dlf-player-progressbar-time').html(
              pm + ':' + (ps<10?'0'+ps:ps)
            );
          },
          //
          onBufferChange: function(a) {
            if (dlfAudio.ati.didFirePlayEvent) {
              var duration =
                dlfAudio.currentPlayingImage.data('audio-duration') > 0 ?
                parseInt(dlfAudio.currentPlayingImage.data('audio-duration')) :
                parseInt(jwplayer(dlfAudio.playerId).getDuration());
              dlfAudio.ati.bufferposition = a.bufferPercent <= 0 ? 0 :
                (a.bufferPercent / 100 * duration);
              var isbuffering = a.bufferPercent >= 100 ? 0 : 1;
              if (isbuffering != dlfAudio.ati.isbuffering) {
                dlfAudio.ati.isbuffering = isbuffering;
                dlfAudio.fireATIRequest(
                  'info&buf=' + isbuffering,
                  dlfAudio.ati.position,
                  dlfAudio.ati.startposition,
                  dlfAudio.ati.bufferposition,
                  dlfAudio.playerId
                );
              }
            }

          },
          onSeek: function(a) {
