            dlfAudio.ati.position = a.offset;
            dlfAudio.fireATIRequest(
              'move',
              dlfAudio.ati.position,
              dlfAudio.ati.startposition,
              dlfAudio.ati.bufferposition,
              dlfAudio.playerId
            );
            dlfAudio.fireIvwRequest();
          },
          onPlaylistItem: function(e) {
            dlfAudio.ati.position = 0;
            dlfAudio.ati.startposition = 0;
            dlfAudio.ati.bufferposition = 0;
            dlfAudio.ati.isbuffering = 1;
          },
          onComplete: function() {
            dlfAudio.fireATIRequest(
              'stop',
              0,
              0,
              dlfAudio.ati.bufferposition,
              dlfAudio.playerId
            );
            dlfAudio.ati.isPlaying = false;
            dlfAudio.ati.didFirePlayEvent = false;
          }
        }
      });
    },

    fireIvwRequest : function() {
      dlfAudio.fireIvwSzmngRequest();
    },

    fireIvwSzmngRequest : function() {
      var offerId = 'dradio',
          categoryCode = dlfAudio.ivw.code,
          comment = '',
          iam_data = {
            "st": offerId, // site/domain
            "cp": categoryCode, // code
            "sv": "ke", // Es wird keine Befragungseinladung ausgeliefert.
            "co": comment
          };

      iom.c(iam_data,1);
    },

    getStation : function(file) {
      var station = file.match(
        /ondemand-mp3\.dradio\.de\/file\/dradio\/\d{4}\/\d{2}\/\d{2}\/(.*?)_/
      );
      if (station != null && typeof(station[1]) != 'undefined') {
        var offset = $.inArray(station[1], dlfAudio.stationCodes);
        if (offset > -1) {
          station = dlfAudio.stationMapping[offset];
        } else {
          station = false;
        }
      } else {
        station = false;
      }
      if(!station) {
        var host = location.hostname;
        $.each(dlfAudio.stationDomains, function(i,e){
          $.each(e, function(index,element){
            if(host.match(element)!=null) {
              station = i;
            }
          });
        });
        if(!station) {
          station = 'DLR';
        }
      }
      return station;
    },

    getIsTeaser : function(file) {
      var result = '';
      if (/_TEASER_/.test(file)) {
        result = ' Teaser';
      }
      return result;
    },

    /**
     *
     * @param string action (play, play&buf=1, info&buf=1, info&buf=0, pause, download, move)
     * @param int position (in seconds)
     * @param int startposition (in seconds)
     * @param int bufferposition (in seconds)
     * @returns nothing
     */
    fireATIRequest : function(action, position, startposition, bufferposition, player) {
      if (typeof (dlfAudio.currentPlayingImage.data('audio-src')
                  .match(/ondemand-mp3\.dradio\.de/)) != 'null') {
        var station = dlfAudio.getStation(dlfAudio.currentPlayingImage.data('audio-src'));
        var diraId = dlfAudio.currentPlayingImage.data('audio-diraid');
        var teaser = dlfAudio.getIsTeaser(dlfAudio.currentPlayingImage.data('audio-src'));
        var positionInfo = 'rmp='+parseInt(position)+
                           '&rmpf='+parseInt(startposition)+
                           '&rmbufp='+parseInt(bufferposition);
        var richname =
          (
            (
              typeof(dlfAudio.currentPlayingImage.data('audio-broadcast')) != 'undefined' &&
              dlfAudio.currentPlayingImage.data('audio-broadcast') != '' &&
              dlfAudio.currentPlayingImage.data('audio-broadcast') != 'undefined'
            ) ?
            dlfAudio.currentPlayingImage.data('audio-broadcast')
            :
            dlfAudio.ati.richname
          );
        richname +=
            (richname != '' ? '::' : '')
          + dlfAudio.currentPlayingImage.data('audio-title');
        richname = richname.replace(/&/g, 'und').replace(/%26/g, 'und');
        richname = station + '::' + richname + teaser;
        if (diraId && diraId != '') {
          richname += ' [' + diraId + ']';
        }
        window.setTimeout(function(){
          xt_rm(
            'audio&plyr='+player,
            xtcustom.category1,
            encodeURIComponent(decodeURIComponent(richname)),
            action,
            '',
            dlfAudio.ati.pollDelayInSeconds,
            dlfAudio.currentPlayingImage.data('audio-duration') > 0 ?
              parseInt(dlfAudio.currentPlayingImage.data('audio-duration')) :
              parseInt(jwplayer(player).getDuration()),
            positionInfo,
            '',
            2,
            'int',
            'clip',
            dlfAudio.currentPlayingImage.data('audio-size') > 0 ?
              parseInt(dlfAudio.currentPlayingImage.data('audio-size') / 1000 ) :
              0,
            1
          );
        }, 50);
      }
    },

    xtdl : function() {
      dlfAudio.fireATIRequest(
        'download',
        dlfAudio.ati.position,
        dlfAudio.ati.startposition,
        dlfAudio.ati.bufferposition,
        dlfAudio.playerId
      );
    },

