      MKDRAOD_MODE_THEMA: 3,
      MKDRAOD_MODE_SUCHE: 4,
      MKDRAOD_MODE_FAVORITEN: 5,
      MKDRAOD_MODE_LIVESTREAM: 6,
      MKDRAOD_MODE_ERWEITERTE_SUCHE: 7,
      MKDRAOD_MODE_ERWEITERTE_SUCHE_ERGEBNIS: 8,
      MKDRAOD_MODE_EINZELBEITRAG: 9,
    };
  };

  var _constsStream = function() {
    return {
      MKDRAOD_STREAM_DEUTSCHLANDFUNK: 4,
      MKDRAOD_STREAM_DEUTSCHLANDRADIO_KULTUR: 3,
      MKDRAOD_STREAM_DRADIO_WISSEN: 1,
    };
  };
  
  var _constsStation = function() {
    return {
      MKDRAOD_STATION_DEUTSCHLANDFUNK: 4,
      MKDRAOD_STATION_DEUTSCHLANDRADIO_KULTUR: 3,
      MKDRAOD_STATION_DRADIO_WISSEN: 1,
    };
  };

  var MKDRAOD_AUDIO_NULL = 1,
      MKDRAOD_AUDIO_LIVESTREAM = 2,
      MKDRAOD_AUDIO_BROADCAST = 3;
      
  // Internal functions
  //
  var _entryPoint = function(call) {
      if (!call) {
          // Init with no options
          return methods.init.apply(this, {});
      } else if ((typeof call) != 'string') {
          // Init with given options
          return methods.init.apply(this, arguments);
      } else if ((typeof call) == 'string') {
          // Method call
          return methods[call].apply(this, [].slice.call(arguments, 1));
      }
  };

  var _openAODinternal = function(params) {
    var queryString = '?' + jQuery.param(params),
      windowWidth = 780, windowHeight = 620,
      leftCenter = (window.screen.width / 2) - (windowWidth / 2),
      topCenter = (window.screen.height / 2) - (windowHeight / 2);
      
    $(this).data('mkdraod_interface').openedWindow = window.open(
      $(this).data('mkdraod_interface').parameters.baseURL + queryString,
      $(this).data('mkdraod_interface').parameters.windowName,
      'width=' + windowWidth + ',height=' + windowHeight + ',left=' + leftCenter + ',top=' + topCenter + ',screenX=' + leftCenter + ',screenY=' + topCenter + ',status=no,toolbar=no,location=no,scrollbars=no,menubar=no,resizable=no');
  };

  var _closeAODinternal = function() {
    $(this).data('mkdraod_interface').openedWindow.close()
  };

  // Method definitons
  //
  var methods = {
    init: function(parameters) {
      // Parameters
      var parametersWithDefaults = $.extend(true, {
          'baseURL': 'http://127.0.0.1:8080/window.html', 
          'windowName': 'mkdraod_interface_window'
      }, parameters);

      return this.each(function(){
          // State
          if (!($(this).data('mkdraod_interface'))) {
              $(this).data('mkdraod_interface', {
                  parameters: parametersWithDefaults
              });
          };
      });
    },
    openAODwithStream: function(streamID, state) {
      _openAODinternal.apply(this, [{
        audioMode: MKDRAOD_AUDIO_LIVESTREAM,
